import React, { useEffect, useState } from 'react';

export const Weather = () => {
  const [temperature, setTemperature] = useState(null);
  const [weatherImage, setWeatherImage] = useState(null);

  useEffect(() => {
    fetch('https://api.openweathermap.org/data/2.5/weather?q=Halifax,Canada&APPID=c70084a5e8a2058ce8a54ff0f61a4afe&units=metric')
      .then((response) => response.json())
      .then((data) => {
        const temp = data.main.temp;
        setTemperature(temp);

        // Determine the weather image based on the temperature
        if (temp <= 10) {
          setWeatherImage('../image/cold.png');
        } else if (temp > 10 && temp < 20) {
          setWeatherImage('../image/mild.png');
        } else {
          setWeatherImage('../image/sunny.png');
        }
      });
  });

  return (
    <div>
      <p>{temperature}Â°C</p>
      {weatherImage && <img src={weatherImage} alt="Weather" />}
    </div>
  );
};

export default Weather;
