export const Weather = () => {
  const [temperature, setTemperature] = useState(null);

  useEffect(() => {
    // API request to fetch weather data
    fetch('https://api.openweathermap.org/data/2.5/weather?q=Halifax,Canada&APPID=c70084a5e8a2058ce8a54ff0f61a4afe&units=metric')
      .then((response) => response.json())
      .then((data) => {
        // Extract temperature from the response data
        const temp = data.main.temp;
        setTemperature(temp);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

